import { useState } from "react";
import SearchBar from "./SearchBar";
import TableRow from "./TableRow";
const InventoryTable = (props) => {
  const req = props.maintable;
  const [table,setTable] = useState(req)
  const searchHandler = (keyvalue) =>{
      let newTable = [];
      for (const row of table){
        if (row['name'].includes(keyvalue)){
            newTable.push(row)
        }
      }
      setTable(newTable)
  };

  return (
    <>
      <SearchBar onSearch={searchHandler}/>
      <br />
      <br />
      <br />
      <table style={{background : 'white'}} className="table">
        <thead style={{background:'lightskyblue'}}className="thead-dark">
          <tr>
            <th>Name</th>
            <th>Batch</th>
            <th>stock</th>
            <th>Deal</th>
            <th>Free</th>
            <th>MRP</th>
            <th>Rate</th>
            <th>Expiry Date</th>
          </tr>
        </thead>
        <tbody key={Math.random()} className="thead-light">
          {table.map((row) => (
            <TableRow key ={Math.random()} tableRow={row} />
          ))}
        </tbody>
      </table>
    </>
  );
};

export default InventoryTable;
