import InventoryTable from "./InventoryTable";
const FileData = (props) =>{
    let itemFile = props.file;
    let updatedFile = {}
    for (const record of itemFile){
        // console.log(record,record['name'],updatedFile)
        let val = record['name'].toString()
        // console.log(val,updatedFile[val],updatedFile,"outside if")
        if (updatedFile[val]){
            // console.log(updatedFile[val],updatedFile[val]['batch'])
            updatedFile[val]['batch'].push(record['batch'])
            // console.log(updatedFile[val],updatedFile[val]['batch'])
            updatedFile[val]['stock'] +=+record['stock']
            if ((record['free']*(1.0/record['deal'])) < ( updatedFile[val]['free'] * (1.0/updatedFile[val]['deal']))){

                updatedFile[val]['free'] = record['free']
                updatedFile[val]['deal'] = record['deal']
            }
            let d1= new Date(updatedFile[val]['exp'])
            let d2= new Date(record['exp'])
            // console.log(d1,d2)
            if (d1.getTime() > d2.getTime()){
                updatedFile[val]['exp'] = record['exp']
            }
            if (updatedFile[val]['mrp'] < record['mrp']) {
                updatedFile[val]['mrp'] = record['mrp']
            }
            if (updatedFile[val]['rate'] < record['rate']){
                updatedFile[val]['rate'] = record['rate']
            }
        }
        else{
        
        let obj={}
        obj[val]={
            batch:[record['batch']],
            stock:+record["stock"],
            deal:+record["deal"],
            free:+record["free"],
            mrp:+record["mrp"],
            rate:+record["rate"],
            exp:record["exp"]}
        let newupdatedFile ={ ...obj,
                ...updatedFile
        }
        // console.log(val,newupdatedFile);
        updatedFile= {...newupdatedFile};

         }
       
    };
    console.log(updatedFile)
    const newupdatedFile = []
    for (const key in updatedFile){
        newupdatedFile.push({name:key,...updatedFile[key],batch:updatedFile[key]['batch'][0]})
    }
    console.log(newupdatedFile)
    return <InventoryTable  table={newupdatedFile} />;
}

export default FileData;
