import { useEffect, useState } from "react";
import ReactPaginate from "react-paginate";
import SearchBar from "./SearchBar";
import TableRow from "./TableRow";
const InventoryTable = (props) => {
  const [table, setTable] = useState(props.maintable);
  const [currentItems, setCurrentItems] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  const [itemOffset, setItemOffset] = useState(0);
  useEffect(() => {
    const endOffset = itemOffset + itemsPerPage;
    setCurrentItems(table.slice(itemOffset, endOffset));
    setPageCount(Math.ceil(table.length / itemsPerPage));
  }, [itemOffset, itemsPerPage]);
  const searchHandler = async (keyvalue) => {
    setPageNo(0);
    let newTable = [];
    for (const row of props.maintable) {
      if (row["name"].toLowerCase().includes(keyvalue.toLowerCase())) {
        newTable.push(row);
      }
    }
    setTable(newTable);
  };
  const handlePageClick = (event) => {
    const newOffset = (event.selected * itemsPerPage) % table.length;
    setItemOffset(newOffset);
  };
  return (
    <>
      <SearchBar onSearch={searchHandler} />
      <br />
      <br />
      <br />
      <table style={{ background: "white" }} className="table">
        <thead style={{ background: "lightskyblue" }} className="thead-dark">
          <tr>
            <th>Name</th>
            <th>Batch</th>
            <th>stock</th>
            <th>Deal</th>
            <th>Free</th>
            <th>MRP</th>
            <th>Rate</th>
            <th>Expiry Date</th>
          </tr>
        </thead>
        <tbody className="thead-light">
          {currentItems.map((row) => (
            <TableRow key={row.name + row.batch} tableRow={row} />
          ))}
        </tbody>
      </table>
      {table.length <= 0 && (
        <h2
          style={{ width: "100%", alignContent: "center", marginLeft: "35%" }}
        >
          Enter Valid Medicine Name
        </h2>
      )}
      <div style={{alignItems : 'self-end' }}>
        <ReactPaginate
          breakLabel="..."
          nextLabel="next >"
          onPageChange={handlePageClick}
          pageRangeDisplayed={5}
          pageCount={pageCount}
          previousLabel="< previous"
          renderOnZeroPageCount={null}
        />
      </div>
    </>
  );
};

export default InventoryTable;
