import { useRef } from "react";
const SearchBar = (props) => {
    const searchRef = useRef();
    const searchHandler = () => {
        props.onSearch(searchRef.current.value)
    };
  return (
    <div class="input-group" >
      <div class="form-outline" style={{border : '2px solid',marginLeft:"40%" } }>
        <input  ref={searchRef } type="search" id="form1" class="form-control" />
        <label class="form-label" for="form1">
          Search
        </label>
      </div>
      <button type="button" class="btn btn-primary" onClick={searchHandler}>
        <i class="fas fa-search"></i>
      </button>
    </div>
  );
};

export default SearchBar;
