import { useState } from "react";
import SearchBar from "./SearchBar";
import TableRow from "./TableRow";
const InventoryTable = (props) => {
  const [table, setTable] = useState(props.maintable);
  const [pageNo,setPageNo] = useState(1);
  const pageChangeHandler = ( event) =>{
    setPageNo(event.target.value)
  }
  const searchHandler = async (keyvalue) => {
    let newTable = [];
    for (const row of props.maintable) {
      if (row["name"].toLowerCase().includes(keyvalue.toLowerCase())) {
        newTable.push(row);
      }
    }
    let st=(pageNo-1)*10
    setTable(newTable.slice(st,st+10));
  };

  return (
    <>
      <SearchBar onSearch={searchHandler} />
      <div style={{"alignItems":'center' }}>
        <input onChange={pageChangeHandler} type='number' min={0} max={table.length/10} ></input>
      </div>
      <br />
      <br />
      <br />
      <table style={{ background: "white" }} className="table">
        <thead style={{ background: "lightskyblue" }} className="thead-dark">
          <tr>
            <th>Name</th>
            <th>Batch</th>
            <th>stock</th>
            <th>Deal</th>
            <th>Free</th>
            <th>MRP</th>
            <th>Rate</th>
            <th>Expiry Date</th>
          </tr>
        </thead>
        <tbody className="thead-light">
          {table.map((row) => (
            <TableRow key={row.name + row.batch} tableRow={row} />
          ))}
        </tbody>
      </table>
      {table.length <= 0 && (
            <h2 style={{ width: "100%" ,alignContent:"center",marginLeft:"35%"}}>Enter Valid Medicine Name</h2>
          )}
    </>
  );
};

export default InventoryTable;
