import { useRef, useState } from "react";
import FileData from './components/FileData'
import Papa from "papaparse";
import "./index.css";
function App() {
  const inputRef = useRef();
  const [hasUploaded, setHasUploaded] = useState(false);
  const [file,setfile] = useState([]);
  console.log('hi',file,file.length)
  if (file.length > 0){
    console.log('length is more than 0',file);
    <FileData inventroy_data={file} />
  }
  const fileSubmitHandler = (event) => {
    event.preventDefault();
    if (!inputRef.current.value){
       alert("PLEASE SELECT A FILE")
       return;
    }
    Papa.parse(inputRef.current.files[0],{
      header:true,
      skipEmptyLines :false,
      complete : (results) =>{
          setfile(results.data)
      }
  });
  };
  const inputfillHandler = () => {
    setHasUploaded(true);
  };
  let inputcolour = hasUploaded ? "btn btn-success" : "btn btn-secondary";
  return (
    <>
    <form className="form" onSubmit={fileSubmitHandler}>
      <h1>UPLOAD THE INVENTORY DATA</h1>
      <br />
      <br />
      <label
        htmlFor="fileuploadinput"
        className={`btn centerfill ${inputcolour}`}
      >
        {hasUploaded ? "FILE SELECTED" : "UPLOAD THE DATA FILE HERE"}{" "}
      </label>
      <input
        onChange={inputfillHandler}
        ref={inputRef}
        className="btn btn-secondary"
        style={{ visibility: "hidden" }}
        type="file"
        id="fileuploadinput"
      />
      <br></br>
      <br></br>
      <button type="submit" className="btn btn-primary">
        SUBMIT THE FILE
      </button>
    </form>
    </>
  );
}

export default App;
