import { useRef, useState } from "react";
import FileData from "./components/FileData";
import "./index.css";
function App() {
  const inputRef = useRef();
  const [hasUploaded, setHasUploaded] = useState(false);
  const fileSubmitHandler = (event) => {
    event.preventDefault();
    if (!inputRef.current.value){
       alert("PLEASE SELECT A FILE")
       return;
    }
    console.log(inputRef.current.value);
    <FileData file={inputRef.current.value} />;
  };
  const inputfillHandler = () => {
    setHasUploaded(true);
  };
  let inputcolour = hasUploaded ? "btn btn-success" : "btn btn-secondary";
  return (
    <form className="form" onSubmit={fileSubmitHandler}>
      <h1>UPLOAD THE INVENTORY DATA</h1>
      <br />
      <br />
      <label
        htmlFor="fileuploadinput"
        className={`btn centerfill ${inputcolour}`}
      >
        {hasUploaded ? "FILE SELECTED" : "UPLOAD THE DATA FILE HERE"}{" "}
      </label>
      <input
        onChange={inputfillHandler}
        ref={inputRef}
        className="btn btn-secondary"
        style={{ visibility: "hidden" }}
        type="file"
        id="fileuploadinput"
      />
      <br></br>
      <br></br>
      <button type="submit" className="btn btn-primary">
        SUBMIT THE FILE
      </button>
    </form>
  );
}

export default App;
